mapper.select:
select
,

from b_member member, b_member_profile memberProfile
where member.id = memberProfile.id

   AND member.user_no = #{map.EQ_userNo}
   AND member.username = #{map.EQ_username}
   AND member.mobile_no = #{map.EQ_mobileNo}
   AND member.user_type = #{map.EQ_userType}
   AND member.grade = #{map.EQ_grade}
   AND member.real_name = #{map.EQ_realName}
   AND member.status = #{map.EQ_status}
   AND member.create_time >= #{map.GTE_createTime}
   AND member.create_time <= #{map.LTE_createTime}
   AND memberProfile.manager = #{map.EQ_manager}
   AND memberProfile.broker = #{map.EQ_broker}
   AND memberProfile.inviter = #{map.EQ_inviter}




    ORDER BY member.id DESC
